import React from "react";
import Nav from 'react-bootstrap/Nav';
import {useState} from 'react';

function NavBar() {
  const [isHovering, setIsHovering] = useState(false);

  const handleMouseEnter = () => {
    setIsHovering(true);
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };
  return (
    <Nav justify variant="tabs" defaultActiveKey="/home">
      <Nav.Item>
        <Nav.Link href="/search" style={styles.link}>Search</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/saved" style={styles.link}>Saved</Nav.Link>
      </Nav.Item>

    </Nav>
  );
}

export default NavBar;

const styles = {
  link: {
    color: "#135d66",
    backgroundColor: "#38A9C8",
    padding: "10px",
    border: "1px solid #135d66",
    borderRadius: 0,
    backgroundColor: isHovering ? 'salmon' : '',
    color: isHovering ? 'white' : '',
  }
}
